import java.util.List;
import java.util.ArrayList;
import java.util.Map;
import java.io.File;
import java.nio.file.Files;
import java.nio.file.Paths;

public class App {
    public static void main(String[] args) {
        Pulumi.run(App::stack);
    }

    public static void stack(Context ctx) {
        var exampleApplication = new Application("exampleApplication");

        var exampleKubernetes = new Kubernetes("exampleKubernetes", KubernetesArgs.builder()        
            .appId(exampleApplication.id())
            .helmVersion("V3")
            .description("Service for deploying Kubernetes manifests")
            .variables(            
                KubernetesVariableArgs.builder()
                    .name("test")
                    .value("test_value")
                    .type("TEXT")
                    .build(),
                KubernetesVariableArgs.builder()
                    .name("test2")
                    .value("test_value2")
                    .type("TEXT")
                    .build())
            .build());

    }
}
